INSERT INTO `president` (`id`, `president`, `school_id`, `introduce`, `pic`, `praise`, `posization`) VALUES
(1, '陈浩', 1, '陈浩，男，预备党员，重庆邮电大学IT精英班专业2013级本科生，现任重庆邮电大学学生会副主席。曾获重庆邮电大学优秀学生三等奖学金、IT精英培养资助计划奖学金、“TI杯”重庆市大学生电子设计竞赛优秀奖、重庆邮电大学大学生数学建模三等奖等荣誉。', 'http://redrock.u.qiniudn.com/2015-09-24_56040a985963c.jpg-tinyq30', '0', '学生会副主席'),
(3, '田兰', 40, '田兰，女，共青团员，重庆幼儿师范高等专科学校2014级专科生，现任重庆幼儿师范高等专科学校学生会副主席。在学校第二届运动会中荣获“优秀裁判员”。首届校园之星“书法之星”选拔大赛中荣获“三等奖”“微课之星”荣获“三等奖”。曾获得“优秀团干部”光荣称号。', 'http://redrock.u.qiniudn.com/2015-09-24_56040ba7d7af7.jpg-tinyq30', '0', '重庆幼儿师范高等专科学校学生会副主席'),
(4, '陈曦', 40, '陈曦，女，共青团员，重庆幼儿师范高等专科学校学前教育2013级专科生，现任重庆幼儿师范高等专科学校校学生会主席，曾被评为2014——2015学年度共青团优秀团干部；“党的十八大四中全会精神”宣传PPT制作、讲解大赛中，荣获一等奖；“让青春构建和谐校园”演讲比赛一等奖；2014年全区“中华魂”（放飞梦想）青少年读书活动征文比赛（青年组）二等；“我的学前梦”演讲比赛中，荣获一等奖；5.25心理手抄报三等奖。', 'http://redrock.u.qiniudn.com/2015-09-24_56040bf0493ff.jpg-tinyq30', '0', '重庆幼儿师范高等专科学校校学生会主席'),
(5, '唐道珍', 40, '唐道珍，女，共青团员，重庆幼儿师范高等专科学校2014级专科生，现任重庆幼儿师范高等专科学校学生会副主席。大学军训期间刻苦训练获得“优秀学员”的称号。在学校第二届运动会中荣获“优秀裁判员”。首届校园之星之“微课之星”选拔大赛中获得“优秀奖”。学校举办的第三届大学生成长论坛“青春微课堂”比赛获得“一等奖”，并代表学校参加万州区片区比赛获得三等奖。从事学生工作获得“优秀团干部”称号。参加系部“我的学前梦”演讲比赛荣获“第一名”。学校举行的“践行核心价值观，凝聚中华正能量”主题演讲比赛获得“三等奖”。在学校举办', 'http://redrock.u.qiniudn.com/2015-09-24_56040c2e780b1.jpg-tinyq30', '0', '重庆幼儿师范高等专科学校学生会副主席'),
(6, '谭抒琴', 39, '谭抒琴，女，共青团员，重庆轻工职业学院营养与食品卫生专业，2014级专科生，现任重庆轻工职业学院院学生会主席团成员。', 'http://redrock.u.qiniudn.com/2015-09-24_56040c6dcb691.jpg-tinyq30', '0', '重庆轻工职业学院院学生会主席团成员'),
(7, '胡军安', 39, '胡军安，女，共青团员，重庆轻工职业学院会展策划与管理专业2014级专科生，现任重庆轻工职业学院院学生会主席团成员。', 'http://redrock.u.qiniudn.com/2015-09-24_56040c9cf423b.jpg-tinyq30', '0', '重庆轻工职业学院院学生会主席团成员'),
(8, '邓帅', 38, '邓帅，男，共青团员，重庆公共运输职业学院轨道控制专业2014级专科生，现任重庆公共运输职业学院学生会副主席。曾被评为2014—2015学期“优秀学生”，在新生军训中被评为“优秀学员”，在诵读比赛中被评为 优秀组织者。在大一这一年中担任班长和院广播站播音员等职务。参加学院党校学习成为入党积极分子。不论在学习中还是工作上，我始终让自己保持着积极向上的态度，让自己都能时刻准备着迎接新的挑战。并且自己拥有着强大的组织能力和团队协调能力能够很好的调动团队积极性提高团队工作效率。我也相信成功永远属于一直在跑的人，我们', 'http://redrock.u.qiniudn.com/2015-09-24_56040ce9e1b18.JPG-tinyq30', '0', '重庆公共运输职业学院学生会副主席'),
(9, '钟明静', 38, '钟明静，女，共青团员，重庆公共运输职业学院城市交通运输专业2014级专科生，现任重庆公共运输职业学院学生会主席。曾被评为2014年“优秀共青团员”，2014年“大学生预防艾滋病”演讲比赛二等奖，“第二届中华经典诵读”比赛三等奖等等。身为主席团的一员，我一直秉承没有拖沓繁复的规划，并且所有的计划都会从实际着手。认真做好每一期的工作，办好每一次的活动，树立好每一回引领的旗帜，上传下达好每一份总结。积极和他系做好交际，取长补短，带领这一届学院学生会尽自己最大所能向着辉煌的道路前进。', 'http://redrock.u.qiniudn.com/2015-09-24_56040d3c96908.jpg-tinyq30', '0', '重庆公共运输职业学院学生会主席'),
(10, '段佳丽', 38, '段佳丽，女，共青团员，重庆公共运输职业学院轨道电气控制专业2014级专科生，现任公共运输职业学院学生会副主席。曾被评为“优秀学生干部”军训优秀学员” ，大一这一年我在班上担任“学习委员”一职，在学生会里担任“学习部干事”。活泼开朗、乐观向上的我在工作上脚踏实地、认真负责，勇于迎接新挑战。我有较强的组织能力和活动策划能力，有较强的语言表达能力，还有较强的团队精神。不曾经历风雨怎能见彩虹，没有人能够随随便便成功。学习工作都需要付出，付出有苦，也有乐，没有终点，只有起点，没有最好，只更好。因为我始终奉持着一种信', 'http://redrock.u.qiniudn.com/2015-09-24_56040f6e6723d.jpg-tinyq30', '0', '公共运输职业学院学生会副主席'),
(11, '廖珊', 37, '廖珊，女，共青团员，重庆旅游职业学院2014级旅游管理系导游专业学生，现任重庆旅游职业学院学生会副主席。曾被评选为“军训优秀学员”、曾荣获学校导游大赛二等奖。', 'http://redrock.u.qiniudn.com/2015-09-24_5604108de39f4.jpg-tinyq30', '0', '学生会副主席'),
(12, '罗琳', 37, '罗琳，女，共青团员，重庆旅游职业学院2014级旅游管理系导游专业学生，现任重庆旅游职业学院学生会主席。曾荣获濯水古镇主题演讲比赛优秀奖、第二届大学生职场模拟招聘大赛三等奖、学生会每月之星。', 'http://redrock.u.qiniudn.com/2015-09-24_560410f24971e.png-tinyq30', '0', '重庆旅游职业学院学生会主席'),
(13, '谭遥尧', 37, '谭遥尧，男，共青团员，重庆旅游职业学院2014级旅游装备系汽车检测与维修专业学生，现任重庆旅游职业学院学生会副主席，兼音乐社社长。多次负责学校的活动，组织、策划，喜爱音乐并精通多门乐器，曾获学院歌手大赛三等奖。', 'http://redrock.u.qiniudn.com/2015-09-24_56041128c964e.jpg-tinyq30', '0', '重庆旅游职业学院学生会副主席'),
(14, '李爽', 36, '李爽，女，共青团员，重庆交通职业学院城市交通运输专业2014级学生，现任重庆交通职业学院学生会副主席，曾被评为城市交通运输协会先进个人，学院第六届阳光杯足球赛迎新杯篮球赛优秀组织工作者，学院第八届技能大赛第二名，学院春季田径运动会400米跳远第二名。', 'http://redrock.u.qiniudn.com/2015-09-24_5604119ef0d49.jpg-tinyq30', '0', '重庆交通职业学院学生会副主席'),
(15, '罗洪生', 36, '罗洪生  男，共青团员，重庆交通职业学院建筑工程技术2014级学生。现任重庆交通职业学院学生会主席，曾获得2014年度优秀共青团干部荣誉称号、获得“优秀干事”荣誉称号、在2014年学院技能大赛混凝土强度大赛中获得第一名、两次代表学校外出参加市级比赛（重庆市2015大学生田径运动会、重庆市黑山谷大学生越野赛）、校运动会中获得男子800米第二名、在2014年读书月活动中获得优秀奖、在校冬季长跑活动中获得第五名、在第六届”迎新杯“篮球赛中获得团体第三名。', 'http://redrock.u.qiniudn.com/2015-09-24_560411ee6528a.jpg-tinyq30', '0', '重庆交通职业学院学生会主席'),
(16, '罗中军', 36, '罗中军  男，共青团员，重庆交通职业学院建筑工程技术专业2014级学生，现任重庆交通职业学院学生第一副书记。 曾获得学院第六届校园文化艺术节“青春梦想汇”个人风采大赛建筑系“第一名”学院“优秀奖”荣誉称号；获得2014-2015学年第一期学院“优秀学生” 荣誉称号；获得2014-2015学年系部“优秀团干部”荣誉称号。', 'http://redrock.u.qiniudn.com/2015-09-24_560412204ec00.jpg-tinyq30', '0', '重庆交通职业学院学生第一副书记'),
(17, '秦朗', 36, '秦朗、男、共青团员、重庆交通职业学院建筑工程技术专业2014级。现任重庆交通职业学院学生会副主席，在班级中担任班长及辅导员助理。曾多次参加志愿者活动及社会实践活动。', 'http://redrock.u.qiniudn.com/2015-09-24_5604125cad5fd.jpg-tinyq30', '0', '重庆交通职业学院学生会副主席'),
(18, '王昕梓', 36, '王昕梓、男、共青团员，重庆交通职业学院建筑工程造价2014学生。现任重庆交通职业学院学生会副主席。曾获得；贵州第27届科技创新大赛，市二等奖、省三等奖。', 'http://redrock.u.qiniudn.com/2015-09-24_5604128e66ff5.jpg-tinyq30', '0', '重庆交通职业学院学生会副主席'),
(20, '杨恩', 36, '杨恩，男，共青团员，现任重庆交通职业学院学生第二副书记。曾被评为优秀团员干部、军训优秀学员标兵、学生会优秀工作者，曾获重庆交通职业学院车辆工程系第四届模拟招聘会第二名、第一届诚信考试演讲比赛第一名。曾参加中共沿河土家族自治县共产主义青年团第18届代表大会，并获中共沿河土家族自治县共青团委员会候选人（草案）提名。', 'http://redrock.u.qiniudn.com/2015-09-24_560412e373f17.jpg-tinyq30', '0', '重庆交通职业学院学生第二副书记'),
(21, '郑博文', 36, '郑博文，男，共青团员，重庆交通职业学院道路桥梁工程造价2014级学生，现任重庆交通职业学院学生会副主席。曾获得2014级新生军训“标兵”称号、曾多次参与志愿者活动，在班级担任团支部书记。', 'http://redrock.u.qiniudn.com/2015-09-24_56041313bf081.jpg-tinyq30', '0', '重庆交通职业学院学生会副主席'),
(22, '郑博文', 36, '郑博文，男，共青团员，重庆交通职业学院道路桥梁工程造价2014级学生，现任重庆交通职业学院学生会副主席。曾获得2014级新生军训“标兵”称号、曾多次参与志愿者活动，在班级担任团支部书记。', 'http://redrock.u.qiniudn.com/2015-09-24_5604131b42b00.jpg-tinyq30', '0', '重庆交通职业学院学生会副主席'),
(23, '郑博文', 36, '郑博文，男，共青团员，重庆交通职业学院道路桥梁工程造价2014级学生，现任重庆交通职业学院学生会副主席。曾获得2014级新生军训“标兵”称号、曾多次参与志愿者活动，在班级担任团支部书记。', 'http://redrock.u.qiniudn.com/2015-09-24_5604131c84ada.jpg-tinyq30', '0', '重庆交通职业学院学生会副主席'),
(24, '王艺多', 35, '王艺多，女，共青团员，重庆传媒职业学院影视系电视节目制作专业2014级专科生，现任重庆传媒学院学生会副主席。曾获得2010年西昌美术节优秀奖、2011年荣获三等奖学金、2013三好学生、优秀学生干部、2014年九月获学院“军训标兵”、2014年加入学生会成为干事、2015年获优秀学生干事。在实践活动中参与西昌火把节成为荣誉志愿者、参与成都舞蹈大赛、参与牡丹园活动成为志愿者和参与学校大大小小各项活动。', '-tinyq30', '0', '重庆传媒学院学生会副主席'),
(25, '甘林', 1, '甘林，男，共青团员，重庆邮电大学动画专业2013级本科生，现任重庆邮电大学学生会副主席。主持操办学校“草坪音乐会”“十大歌手赛”“重邮梦想秀”等多个大型文体活动，活动质量个效果获得广泛好评，曾获标牌大赛第一名。', 'http://redrock.u.qiniudn.com/2015-09-24_56041380914b0.jpg-tinyq30', '0', '学生会副主席'),
(26, '伍丹丹', 35, '伍丹丹，女，共青团员，重庆传媒职业学院新闻系传媒策划与管理专业2014级专科生，现任重庆传媒学院学生会副主席。曾获得2012曾被评为优秀班干、优秀社团干部、2013曾被评为优秀社团联合会干部、2014年九月获学院“军训标兵”。在实践活动中参加了学校各大小型活动。', '-tinyq30', '0', '重庆传媒学院学生会副主席'),
(27, '梁佳妮', 1, '梁佳妮，女，共青团员，重庆邮电大学艺术设计专业2013级本科生，现任重庆邮电大学学生会副主席。曾获重庆邮电大学优秀学生二等奖学金；被评为“ 三好学生”、“优秀共青团干部”、第八届涂鸦大赛优秀奖等荣誉。', 'http://redrock.u.qiniudn.com/2015-09-24_560413d0acb2c.jpg-tinyq30', '0', '学生会副主席'),
(28, '周健', 35, '周健，男，共青团员，重庆传媒职业学院艺术系室内设计专业2014级专科生，现任重庆传媒学院学生会副主席。曾获得2012年获铜梁县第十六届艺术暨比赛一等奖、2013年第五届春季运动会获男子跳远第一名、2014年九月获学院“军训标兵”、4014年获重庆市第四届大学生艺术展演活动一等奖、2014年加入学生会成为体育部干事。在实践活动中参与重庆市大学生艺术展活动、参与重庆市大学生创业培训、参与牡丹园活动成为志愿者和学校各大大小小活动。', '-tinyq30', '0', '重庆传媒学院学生会副主席'),
(29, '朱琳', 35, '朱琳，女，共青团员，重庆传媒职业学院新闻系传媒策划与管理专业2013级专科生，现任重庆传媒学院学生会主席。曾在2013年被评校优秀学生干事、三好学生，2014年被评三好学生、优秀学生干部，2015年被评技能大赛优秀奖等。在学生会这两年里参加了校外实践活动有：自愿者敬老院活动、三下乡活动、牡丹园开园活动等。在校内参加和组织的活动有：迎新晚会、五四红歌比赛、运动会、校园之春歌手大赛等各大大小小活动。', '-tinyq30', '0', '重庆传媒学院学生会主席'),
